def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
def holder = groovyUtils.getXmlHolder( "SOAP Request#Response" )
def errors = holder["count(//*:SpellResult/*:error)"]
assert errors== "2"

def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
def holder = groovyUtils.getXmlHolder( "SOAP Request#Response" )
def rightSuggestionFirstWord = holder.getNodeValue("//*:SpellResult/*:error/*:s")
def rightSuggestionSecondWord = holder.getNodeValue("//*:SpellResult/*:error[2]/*:s")

assert rightSuggestionFirstWord == "привет"
assert rightSuggestionSecondWord == "дела"
